import clsx from 'clsx';
import React from 'react';
import {
	cardDifficultySmallContainer,
	difficultyContainer
} from '../../../styles/styles';

const Difficulty = (difficulty) => {
	difficulty = 'Easy';

	switch (difficulty) {
		case 'Easy':
			return (
				<div
					className={clsx(
						cardDifficultySmallContainer,
						'border-[#168914]',
						'bg-[#03b1001f]'
					)}
				>
					<h5 className={clsx('nunito-sans-normal', 'text-xl', 'text-[#168914]')}>
						Easy
					</h5>
				</div>
			);
			break;
		case 'Medium':
			return (
				<div
					className={clsx(
						cardDifficultySmallContainer,
						'border-[#C65F00]',
						'bg-[#03b1001f]'
					)}
				>
					<h5 className={clsx('nunito-sans-normal', 'text-xl', 'text-[#168914]')}>
						Easy
					</h5>
				</div>
			);
			break;
		case 'Easy':
			return (
				<div
					className={clsx(
						cardDifficultySmallContainer,
						'border-[#168914]',
						'bg-[#03b1001f]'
					)}
				>
					<h5 className={clsx('nunito-sans-normal', 'text-xl', 'text-[#168914]')}>
						Easy
					</h5>
				</div>
			);
			break;
	}
};

export default Difficulty;
