import * as a from './actionTypes';

const initialState = [];

const recipesReducer = (state = initialState, action) => {
	switch (action.type) {
		case a.FILL_INITIALLY:
			// return action.payload;

			return state.concat(action.payload);
		case a.LOAD_MORE:
			return state.concat(action.payload);

		default:
			return state;
	}
};

export default recipesReducer;
