import Home from './Home';

function App() {
 const pizza = useSelector((state) => state.pizza);
 const dispatch = useDispatch();

 return (
  <Router>
   <Routes>
    <Route path="/">
     <Home />
    </Route>
    <Route path="/product">
     <Home />
    </Route>
   </Routes>
  </Router>
 );
}

export default App;

